import React, { Component } from 'react';
import './App.css';
import SearchForm from './SearchForm';
import Nav from './Nav';
import Gallery from './Gallery';

 class App extends Component {

  constructor() {
    super();
    this.state = {
      gifs: [],
      loading: true
    };
  } 

    getSearchData = () => {
      fetch(`https://www.flickr.com/services/rest/?method=flickr.photos.getRecent&api_key=be062c97a8705f46019b77f666db9c5d&per_page=10&format=json&nojsoncallback=true`)
        .then(response => response.json())
        .then(responseData => {
          this.setState({ gifs: responseData.data });
        })
        .catch(error => {
          console.log('Error fetching and parsing data', error);
        });
    }
  render (){
    console.log(this.state.gifs);
    return (
      <div className="container">
        <SearchForm />
        <Nav />
        <Gallery />

      </div>
    )
  }


}

export default App;
