import React, { Component } from 'react';
import './App.css';
import { apiKey } from './Key';
import axios from 'axios';
import SearchForm from './SearchForm';
import Nav from './Nav';
import Gallery from './Gallery';
import {
  BrowserRouter,
  Route
} from 'react-router-dom';


 class App extends Component {
  constructor() {
    super();
    this.state = {
      photos: [],
      loading: true
    };
  } 

  componentDidMount() {
    this.preformSearch()
    }

  preformSearch(query='kitty'){
    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=${query}&per_page=24&page=1&format=json&nojsoncallback=1`)
  .then(response => {

    this.setState({
      photos: response.data.photos.photo,
      loading: false,
    });
  })
     .catch(error => {
     console.log('Error fetching and parsing data', error);
     });
  }

  render (){
    console.log(this.state.photos);
    return (
      <BrowserRouter>  
        <div className="container">
        <Route path="/about" render={ () => 
          <About title='About' />,
          <SearchForm onSearch={this.preformSearch}/>,
          <Nav />, 
          <Gallery value={this.state.photos}/>} />

        </div>
      </BrowserRouter>
    )
  }


}

export default App;
