import React, { Component } from 'react';
import './App.css';
import SearchForm from './SearchForm';
import Nav from './Nav';
import Gallery from './Gallery';
import axios from 'axios';
//import { apiKey } from './Key';

 class App extends Component {

  constructor() {
    super();
    this.state = {
      photos: [],
      loading: true
    };
  } 

    getSearchData = () => {
      axios.get('https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=be062c97a8705f46019b77f666db9c5d&tags=cat&per_page=24&format=json&nojsoncallback=1')
        .then(response => {
          console.log(response);
          this.setState({
            photos: response.data.data
          });
        })  
        .catch(error => {
          console.log('Error fetching and parsing data', error);
        });
    }
  render (){
    console.log(this.state.photos);
    return (
      <div className="container">
        <SearchForm />
        <Nav />
        <Gallery />

      </div>
    )
  }


}

export default App;
