import React from 'react';
import './App.css';
import axios from 'axios';
import SearchForm from './SearchForm';
import Nav from './Nav';
import Gallery from './Gallery';

 class App extends Component {

  constructor() {
    super();
    this.state = {
      gifs: [],
      loading: true
    };
  } 

  getSearchData = () => {
    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.getRecent&api_key=be062c97a8705f46019b77f666db9c5d`)
      .then(response => {
        this.setState({
          gifs: response.data.data,
          loading: false
        });
      })
      .catch(error => {
        console.log('Error fetching and parsing data', error);
      });    
  }

  render (){
    console.log(this.state.gifs);
    return (
      <div className="container">
        <SearchForm />
        <Nav />
        <Gallery />

      </div>
    )
  }


}
